	// process buttons
	if($AllowInsert) {
		if(!$selected_id) $templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return Posts_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return Posts_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if(Request::val('Embedded')) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate) {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return Posts_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($separateDV ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#caregoria_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#caregoria_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#tag_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#tag_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#title').replaceWith('<div class=\"form-control-static\" id=\"title\">' + (jQuery('#title').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#data').parents('.input-group').replaceWith('<div class=\"form-control-static\" id=\"data\">' + (jQuery('#data').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#midia').replaceWith('<div class=\"form-control-static\" id=\"midia\">' + (jQuery('#midia').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$locale = isset($Translation['datetimepicker locale']) ? ", locale: '{$Translation['datetimepicker locale']}'" : '';
		$jsEditable .= "\tjQuery('#data').addClass('always_shown').parents('.input-group').datetimepicker({ toolbarPlacement: 'top', sideBySide: true, showClear: true, showTodayButton: true, showClose: true, icons: { close: 'glyphicon glyphicon-ok' }, format: AppGini.datetimeFormat('dt') {$locale} });";
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(caregoria_id)%%>', $combo_caregoria_id->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(caregoria_id)%%>', $combo_caregoria_id->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(caregoria_id)%%>', urlencode($combo_caregoria_id->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(tag_id)%%>', $combo_tag_id->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(tag_id)%%>', $combo_tag_id->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(tag_id)%%>', urlencode($combo_tag_id->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['caregoria_id' => ['Categoria', 'Caregoria id'], 'tag_id' => ['Tags', 'Tag id'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(caregoria_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(tag_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(title)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(conteudo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(data)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(midia)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="midia" id="midia" data-filetypes="jpg|jpeg|gif|png" data-maxsize="1048576" accept=".jpg,.jpeg,.gif,.png">'), $templateCode);
	if($AllowUpdate && $row['midia'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(midia)%%>', '<br><input type="checkbox" name="midia_remove" id="midia_remove" value="1"> <label for="midia_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(midia)%%>', '', $templateCode);
	}

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(caregoria_id)%%>', safe_html($urow['caregoria_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(caregoria_id)%%>', html_attr($row['caregoria_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(caregoria_id)%%>', urlencode($urow['caregoria_id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(tag_id)%%>', safe_html($urow['tag_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(tag_id)%%>', html_attr($row['tag_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tag_id)%%>', urlencode($urow['tag_id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(title)%%>', safe_html($urow['title']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(title)%%>', html_attr($row['title']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(title)%%>', urlencode($urow['title']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(conteudo)%%>', '<textarea name="conteudo" id="conteudo" rows="5">' . safe_html(htmlspecialchars_decode($row['conteudo'])) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(conteudo)%%>', '<div id="conteudo" class="form-control-static">' . $row['conteudo'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(conteudo)%%>', nl2br($row['conteudo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(conteudo)%%>', urlencode($urow['conteudo']), $templateCode);
		$templateCode = str_replace('<%%VALUE(data)%%>', app_datetime($row['data'], 'dt'), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(data)%%>', urlencode(app_datetime($urow['data'], 'dt')), $templateCode);
		$row['midia'] = ($row['midia'] != '' ? $row['midia'] : 'blank.gif');
		if( $dvprint) $templateCode = str_replace('<%%VALUE(midia)%%>', safe_html($urow['midia']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(midia)%%>', html_attr($row['midia']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(midia)%%>', urlencode($urow['midia']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(caregoria_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(caregoria_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(tag_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tag_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(title)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(title)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(conteudo)%%>', '<textarea name="conteudo" id="conteudo" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(data)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(data)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(midia)%%>', 'blank.gif', $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Posts');
	if($selected_id) {
		$jdata = get_joined_record('Posts', $selected_id);
		if($jdata === false) $jdata = get_defaults('Posts');
		$rdata = $row;
	}
	$templateCode .= loadView('Posts-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Posts_dv
	if(function_exists('Posts_dv')) {
		$args=[];
		Posts_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}